set Old_promotion_price = (SELECT promotion_price FROM products WHERE id = new.id_product);

-- Get value image product
set Old_image = (SELECT image FROM products WHERE id = new.id_product);



set Level1 = (SELECT level FROM users WHERE id = Id_last_user_action);
set Name_people_action = (SELECT full_name FROM users WHERE id = Id_last_user_action);

UPDATE `thoi_trang`.`products` SET `count_remain` = New_count_remain WHERE (`id` = new.id_product);


if ( Level1 = 1) then
	set Chuc_vu = 'admin';
else if (Level1 = 2) then
